import { Injectable } from "@angular/core";
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/environments/environment';

@Injectable({
    providedIn: "root"
})

export class Ser{
    private apiUrl: any = environment.mysqlConfig.url;

  constructor(private http: HttpClient) { }

  public setrut(rut: any){
    this.apiUrl.push(`${rut}`)
  }


  getRutInfo(rut: any) {
    const url = `${this.apiUrl}/localhost:3306?rut=${rut}`; // Reemplaza "rutInfo" con la ruta adecuada de tu API

    return this.http.get(url);
  }
}
