import { Component, OnInit } from '@angular/core';
import { RutService } from '../rut.service';

@Component({
  selector: 'app-rut-info',
  templateUrl: './rut-info.component.html',
  styleUrls: ['./rut-info.component.css']
})
export class RutInfoComponent implements OnInit{
  R: any; // RUT

  constructor(private servicio: RutService){}

  mostrarInfoRut(){
    this.R = this.servicio.getRutInfo()
  }

  ngOnInit(): void {
    this.mostrarInfoRut()
  }

}
